import React from "react";
import { selectCameraImage } from "./features/cameraSlice";
import "./Preview.css";

function Preview() {
  const cameraImage = useSelector(selectCameraImage);
  return (
    <div className="preview">
      <img src={cameraImage} alt="" />
    </div>
  );
}

export default Preview;
